        type: String,
        required: true,
    },
    description: {
        required: true,
        type: String
    },
    amount: {
        type: Number,
        required: true
    },
    duration: {
        type: String
    },
    startdate: {
        type: String
    },
    endDate: {
        type: String
    },
    status: {
        type: String,
