const User = require('../models/Users');
const Profile = require('../models/Profile');
const Business = require('../models/Business')
const Projects = require('../models/Projects');
const Milestones = require('../models/Milestones');
const jwt = require ('jsonwebtoken')
const cryptoRandomString = require ('crypto-random-string');
const {hash, compare } = require('../utilities/bcrypt');
const emailService = require('../utilities/emailService');
const Code = require('../models/verificationCode')
const SME = require('../models/SME');
const nodemailer = require('nodemailer')

const {JWT_SEC, EMAIL_USER} = require ('../utilities/secrets');
const { async } = require('crypto-random-string');


const postNewUser =async (req, res, next) => {
    try {
        const {
          username, 
          email,
          password,
        } = req.body
  
        const user = await User.create({
          username, 
          email,
          password,
          profile : await Profile.create({
            phone:"", 
            firstName:"",
            lastName:"",
            identificationNumber:"",
            address:"",
            emergencyContact:"",
            DOB:"",
            position:"",
          })
        })
        return res.status(201).json(user)
        
    } catch (error) {
        return res.status(500).json({error:error})
    }
    
  }

  const createBusiness = async function(userID, business) {
    const docBusiness = await Business.create(business);
    return User.findByIdAndUpdate(
      userID, 
      { $push: { business: docBusiness._id } },
      { new: true, useFindAndModify: false }
    );
  };

  const postBusiness = async (req, res, next) =>{
    const {
            businessName, 
            location, 
            numberOfEmployees, 
            registrationID, 
            registrationDate,
             businessType,  
             businessDescription, 
             category,  
             annualIncome,
             userID
          } = req.body

          const coy = {
            businessName, 
            location, 
            numberOfEmployees, 
            registrationID, 
            registrationDate,
             businessType,  
             businessDescription, 
             category,  
             annualIncome,
             userID
          };
    try{
      createBusiness(userID, coy)
      return res.status(201).json({
        coy
      })

    }catch(err){
      res.status(500).json({err})
    }
  }
   


